/*******Get all the methods available in testRunner******/

log.info testRunner.metaClass.methods*.name.unique().sort()

class Hello{

	//def is a general way of defining any variable

	def log;
	def context;
	def testRunner;

	//constructor
	def Hello(log, context, testRunner){

		this.log = log
		this.context = context
		this.testRunner = testRunner
	}

	def sayHello(String name){

		log.info ("   Saying Hello...."+name)

	}
}

context.setProperty("Hello", new Hello(log,context,testRunner))

// calling a method sayHello from same testcase
def testStep = testRunner.testCase.getTestStepByName("Hello")

testStep.run(testRunner, context)

context.Hello.sayHello("Chanusha")

//calling method outside of the test case within the different/seperate test case.
project = testRunner.testCase.testSuite.project;
tcase =  project.testSuites["TestSuite 3"].testCases["TestCase 1GroovyScripting"];
hello = tcase.getTestStepByName("Hello");

hello.run(testRunner, context)

context.Hello.sayHello("Chanusha")

//Run a request within same test case

def status = testRunner.runTestStepByName("GetCountryISOCode");
log.info ("  >>>   "+status);
def result = status.getStatus().toString();
/*Unknown value display when we not added assertions 
for validation*/
log.info ("  >>>   "+result);

if(result == "OK"){

	log.info ("   >>>   "+ "Test Passed")
}else{

	log.info ("   >>>   "+ "Test Failed")
}

//Run a requist from different TC or Test Suite

//Access to the test step(request)
project = testRunner.testCase.testSuite.project ; 
tcase = project.testSuites["TestSuite 3"].testCases["TestCase 2CallMethodInSeperateTC"] ; 
tstep = tcase.getTestStepByName("GetCountryISOCode"); 

//Run a request
def status = tstep.run(testRunner, context) 
def result = status.getStatus().toString(); 
log.info (" ---- "+result)

//groovy code to run test case 

//Access the test case which is needed to run
def tCase = testRunner.testCase.testSuite.testCases["TestCase 2CallMethodInSeperateTC"]
def prop = new com.eviware.soapui.support.types.StringToObjectMap()

//Run a test case
runner = tCase.run(null, false);
log.info ("   >>>   "+ runner.getStatus().toString())

//can use following script also
runner2 = tCase.run(prop, true) 
log.info ("   >>>   "+ runner2.getStatus().toString())

//groovy code to loop test cases in a test suite 

def testCases = testRunner.testCase.testSuite.getTestCaseList()

testCases.each{
	log.info("   >>>   "+ it.name)

	if(it.name == "TestCase 3RunFromGroovyScript")
{
	log.info ("   >>>   "+ "This is Test Case 3")
}

//Print current test case name
log.info ("   Test Case Name is : "+ testRunner.testCase.name)
}

//Run TestSuite 2
//If we run the groovy script inside the same test suite or test case loop will generated

//Access the test suite 3 
def testSuite = context.testCase.testSuite.project.testSuites["TestSuite 2"];

//Run testSuite
testSuite.run(null, false);

//How to get Test Suite name
log.info ("   >>>   "+ testSuite.getName().toString() + "   Executed Successfully")

//if goory script runs a project which is groovy script included loop will generated

