con:configuration.scriptText:
//Check Response time
assert messageExchange.timeTaken < 1400

//Check for Endpoint
//log.info messageExchange.getEndpoint()

//Compare and actual Endpoint versus expected
def endPoint = "http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
assert messageExchange.getEndpoint() == endPoint

//check for TimeTaken 
log.info messageExchange.getTimeTaken() 

//check for headers
//Print a header
//log.info (messageExchange.responseHeaders["Content-Length"])
//Assert a header
assert messageExchange.responseHeaders["Content-Length"] != null

//check attachments
assert messageExchange.responseAttachments.length == 0
//log.info messageExchange.responseAttachments.length

//validate response nodes
//groovyUtils is a one of a class in SoapUI
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
//get the request content
def requestHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
//get the response content
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent)
//validate currency name that response contains is correct
def currencyName = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName")
assert currencyName == "Rupees"

//to get response
def resp = messageExchange.requestContentAsXml.toString()
//print response
log.info resp

//get json response
import groovy.json.JsonSlurper
//get the response content in to variable
def responseMessage = messageExchange.response.responseContent
def json = new JsonSlurper().parseText(responseMessage)

//assert node values
//capital and name are nodes in a response medssage
assert json.capital.toString() != null
assert json.name.toString() == "[Aruba]"

//to get the Test Step Name
testStepName = messageExchange.modelItem.testStep.name
log.info testStepName

//to store response as Xml String
xmlHold = messageExchange.responseContentAsXml.toString()

con:soapui-project.con:afterLoadScript:
//load the aetup and teardoen scripts from a file
def su = new File("D:/MY QA PROJECTS/Soap UI Projects/SetUp.txt").text
def td = new File("D:/MY QA PROJECTS/Soap UI Projects/TearDown.txt").text

project.getTestSuiteList().each { testSuite ->
testSuite.getTestCaseList().each { testCase ->
//add setup and tear down script for test case level
testCase.setSetupScript(su) 
testCase.setTearDownScript(td) 
}
}
